import { Page } from './Page';
import { useHistory } from 'react-router-dom';
import thinkingImage from "../assets/images/kursi/thinking.png";
import paperAirplaneImage from "../assets/images/kursi/paperAirplane.png";
import usefulImage from "../assets/images/kursi/useful.png";

interface BoxProps {
  icon: string;
  description: string;
  buttonTitle: string;
  path: string; // Path for the button to navigate
}

function Box({ icon, description, buttonTitle, path }: BoxProps) {
  const history = useHistory();

  const handleClick = () => {
    history.push(path); // Navigate to the specified path
  };

  return (
    <div className="flex flex-col items-center justify-between bg-df p-6 rounded-lg shadow-md h-full">
      <div className="mb-4 flex justify-center">
        {icon === thinkingImage ? (
          <img className="w-14 h-14 filter brightness-0 invert" src={icon} alt="Icon" />
        ) : icon === usefulImage ? (
          <img className="-mt-6 -mb-4 w-24 h-24 filter brightness-0 invert" src={icon} alt="Icon" />
        ) : (
          <img className="w-14 h-14" src={icon} alt="Icon" />
        )}
      </div>
      <div className="flex-grow mb-4 text-center">
        <p className="text-white">{description}</p>
      </div>
      <div className="flex justify-center">
        <button
          className="px-4 py-2 bg-unFocused hover:bg-focused rounded-xl px-4 py-2 transition duration-200 text-white rounded-lg"
          onClick={handleClick} // Call handleClick on button click
        >
          {buttonTitle}
        </button>
      </div>
    </div>
  );
}

export const Kursi = () => {
  return (
    <Page>
      {/* Rest of the code */}
      <div className="container mx-auto px-6 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-3 gap-8">
          <div className="flex flex-col">
            <Box
              icon={thinkingImage}
              description="Kursi, kuros radās sarežģītības un vajadzēja ievērojami vairāk pievērsties"
              buttonTitle="Sarežģītākie kursi"
              path="/sarezgitakie-kursi" // Specify the path for the button
            />
          </div>
          <div className="flex flex-col">
            <Box
              icon={paperAirplaneImage}
              description="Kursi, kuri radīja interesi un izcēlās citu starpā"
              buttonTitle="Interesantākie kursi"
              path="/interesantakie-kursi" // Specify the path for the button
            />
          </div>
          <div className="flex flex-col">
            <Box
              icon={usefulImage}
              description="Kursi, kuri visvairāk varētu noderēt nākotnē"
              buttonTitle="Noderīgākie kursi"
              path="/noderigakie-kursi" // Specify the path for the button
            />
          </div>
        </div>
      </div>
    </Page>
  );
};
