import { BsFillGiftFill } from 'react-icons/bs';
import React, { useContext, useState } from 'react';
import { NavLink } from './NavLink';
import { AuthModal } from './modals/AuthModal/AuthModal';
import { AuthContext, AuthContextType } from '../context/AuthContext';
import { AccountNavDropdown } from './dropdowns/AccountNavDropdown';

export const Navbar = () => {
    const [isAuthOpen, setIsAuthOpen] = useState(false);
    const [authTab, setAuthTab] = useState(1);
    const { isAuthenticated, isLoading, user, getAuthStatus } = useContext(AuthContext) as AuthContextType;

    const openAuthModal = (tab: number) => {
        setIsAuthOpen(true);
        setAuthTab(tab);
    }

    return (
        <nav className=" p-4 shadow bg-white">
            <div className="container mx-auto px-20 flex justify-between items-center">
                    <ul className="flex items-center gap-x-8">
                        <NavLink title="Mans ceļš uz Latvijas Universitāti" href="/" />
                        <NavLink title="Ceļš līdz izvēlei" href="/wishlists" />
                        <NavLink title="Ceļš uz LU DF" href="/wishlist/create" />
                        <NavLink title="Mācību pieredze" href="/about" />
                        <NavLink title="Lietotie resursi" href="/about" />
                    </ul>
            </div>
        </nav>
    );
};
