import { Page } from "./Page";
import { AboutSection } from "../components/AboutSection";
import resursiImage from "../assets/images/celsUzLuDf/resursi.png";
import programminzenierijaImage from "../assets/images/kursi/programminzenierija.png";
import documentImage from "../assets/images/kursi/document.png";
import prakseImage from "../assets/images/kursi/prakse.png";
import { Link } from "react-router-dom";

export const KursiNoder = () => {
    return (
        <Page>
            <div>
                {/* About us, bg image */}
                <div className="relative flex bg-center justify-center items-center bg-[url('assets/images/kursi/kubiks.jpg')] md:h-about h-screen bg-cover bg-no-repeat">
          <h1 className="top-1/3 left-1/3 transform -translate-x-1/2 -translate-y-1/2 text-4xl text-white font-bold text-center font-montserrat absolute">
            Sarežģītākie kursi
          </h1>
        </div>
                {/* Photos, info */}
                <div className="container mx-auto mt-16 ">
                    <div>
                        <AboutSection
                            title="Programminženierija"
                            separator={true}
                            contentFirst={true}
                            content="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce vel ipsum accumsan, gravida nunc in, vehicula purus. Maecenas lobortis nunc nisl, eget interdum dolor convallis et. Aenean a tincidunt augue. Donec porta massa et felis imperdiet sodales. Quisque hendrerit libero ut lectus tempus imperdiet. Duis eget sapien vel quam volutpat sollicitudin. Curabitur non rhoncus lacus. Nulla ut efficitur massa, facilisis semper ante. Fusce scelerisque mauris a diam hendrerit tempor. Sed sagittis nisi ut vestibulum iaculis."
                            icon={<img src={programminzenierijaImage} alt="kurss 1" className="w-full h-auto rounded-3xl" />}
                        />

                        <AboutSection
                            title="Internets, tīkla etiķete un tiesiskais regulējums"
                            separator={true}
                            content="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce vel ipsum accumsan, gravida nunc in, vehicula purus. Maecenas lobortis nunc nisl, eget interdum dolor convallis et. Aenean a tincidunt augue. Donec porta massa et felis imperdiet sodales. Quisque hendrerit libero ut lectus tempus imperdiet. Duis eget sapien vel quam volutpat sollicitudin. Curabitur non rhoncus lacus. Nulla ut efficitur massa, facilisis semper ante. Fusce scelerisque mauris a diam hendrerit tempor. Sed sagittis nisi ut vestibulum iaculis."
                            icon={<img src={documentImage} alt="kurss 2" className="w-full h-auto rounded-3xl" />}
                        />

                        <AboutSection
                            title="Prakse"
                            contentFirst={true}
                            content="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce vel ipsum accumsan, gravida nunc in, vehicula purus. Maecenas lobortis nunc nisl, eget interdum dolor convallis et. Aenean a tincidunt augue. Donec porta massa et felis imperdiet sodales. Quisque hendrerit libero ut lectus tempus imperdiet. Duis eget sapien vel quam volutpat sollicitudin. Curabitur non rhoncus lacus. Nulla ut efficitur massa, facilisis semper ante. Fusce scelerisque mauris a diam hendrerit tempor. Sed sagittis nisi ut vestibulum iaculis."
                            icon={<img src={prakseImage} alt="kurss 3" className="w-full h-auto rounded-3xl" />}
                        />
                    </div>
                    <div className="grid md:grid-cols-2 grid-cols-1 place-items-center mb-16 mt-7 px-6">
                        <div className="md:w-80 md:h-80 w-56 h-56">
                            <img src={resursiImage} alt="resursi" />
                        </div>
                    <div>
                        <Link to="/celsUzLu">
                            <button className="text-white px-4 py-2 rounded bg-unFocused hover:bg-focused transition font-semibold text-base sm:text-lg" >
                                Kā es nokļūstu līdz LU DF?
                            </button>
                        </Link>                            
                    <div className="text-base mx-auto mt-4 mb-4 sm:pl-5 pl-6">
                                Kādi resursi tika izmantoti, lai tiktu galā ar nepieciešamajām problēmām, ar kurām nācās saskarties kursu ietvaros
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Page>
    );
};
