import { useState } from 'react';
import { NavLink } from './NavLink';
import { LanguageSelector } from './LanguageSelector';
import { useTranslation } from 'react-i18next';
import lvData from '../locales/lv.json';
import enData from '../locales/en.json';

export const Navbar = () => {
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!isMobileMenuOpen);
  };

  const { i18n } = useTranslation();

  const handleLanguageChange = (language: string) => {
    if (i18n.language !== language) {
      i18n.changeLanguage(language).then(() => {
      });
    }
  };

  return (
    <nav className="w-full p-4 shadow bg-df">
      <div className="mx-auto flex justify-between items-center">
        <div className="flex items-center">
          <button
            className="block lg:hidden mr-4 focus:outline-none"
            onClick={toggleMobileMenu}
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              {isMobileMenuOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M4 6h16M4 12h16M4 18h16"
                />
              )}
            </svg>
          </button>

          <ul
            className={`${
              isMobileMenuOpen ? 'block' : 'hidden'
            } lg:flex justify-center items-center gap-x-4`}
          >
            <li className="hover:bg-focused rounded-xl px-4 py-2 transition duration-200 font-medium text-base">
              <NavLink title={lvData.navbar1} href="/" />
            </li>
            <li className="hover:bg-focused rounded-xl px-4 py-2 transition duration-200 font-medium text-base">
              <NavLink title={lvData.navbar2} href="/celsLidzIzvelei" />
            </li>
            <li className="hover:bg-focused rounded-xl px-4 py-2 transition duration-200 font-medium text-base">
              <NavLink title={lvData.navbar} href="/celsUzLu" />
            </li>
            <li className="hover:bg-focused rounded-xl px-4 py-2 transition duration-200 font-medium text-base">
              <NavLink title={lvData.navbar1} href="/kursi" />
            </li>
            <li className="hover:bg-focused rounded-xl px-4 py-2 transition duration-200 font-medium text-base">
              <NavLink title={lvData.navbar1} href="/macibuMateriali" />
            </li>
          </ul>
        </div>
        <LanguageSelector
          currentLanguage={i18n.language}
          onLanguageChange={handleLanguageChange}
        />
      </div>
    </nav>
  );
};
