import { Page } from "./Page";
import { useState } from "react";

export const CelsUzLu = () => {
  const [showContent1, setShowContent1] = useState(false);
  const [showContent2, setShowContent2] = useState(false);

  const handleClick1 = () => {
    setShowContent1(!showContent1);
    setShowContent2(false); // Ensure other content is hidden
  };

  const handleClick2 = () => {
    setShowContent2(!showContent2);
    setShowContent1(false); // Ensure other content is hidden
  };

  return (
    <Page>
      {/* ...existing code */}
      <div className="text-center text-3xl mb-12">
        <button
          className="text-white px-4 py-2 rounded bg-unFocused hover:bg-focused transition font-semibold text-base sm:text-lg mr-2"
          onClick={handleClick1}
        >
          Show Content 1
        </button>
        <button
          className="text-white px-4 py-2 rounded bg-unFocused hover:bg-focused transition font-semibold text-base sm:text-lg"
          onClick={handleClick2}
        >
          Show Content 2
        </button>
      </div>

      {showContent1 && (
        <div className="px-4 md:px-12 lg:px-20">
          {/* Render content 1 when showContent1 is true */}
          <h1 className="text-3xl md:text-4xl mt-12 text-center text-gray-800 font-semibold">
            Content 1
          </h1>
          <p className="text-base md:text-lg lg:text-xl mt-4 mb-12 text-gray-600">
            Content 1 description.
          </p>
        </div>
      )}

      {showContent2 && (
        <div className="px-4 md:px-12 lg:px-20">
          {/* Render content 2 when showContent2 is true */}
          <h1 className="text-3xl md:text-4xl mt-12 text-center text-gray-800 font-semibold">
            Content 2
          </h1>
          <p className="text-base md:text-lg lg:text-xl mt-4 mb-12 text-gray-600">
            Content 2 description.
          </p>
        </div>
      )}

      {/* ...remaining code */}
    </Page>
  );
};
