import { useState } from 'react';
import latviaFlagImage from "../assets/images/latvijasBiblioteka/latviaFlag.png";
import englishFlagImage from "../assets/images/latvijasBiblioteka/englishFlag.png";
import lvData from "../locales/lv.json";
import enData from "../locales/en.json";
import { NavLink } from './NavLink';

type LanguageSelectorProps = {
  currentLanguage: string;
  onLanguageChange: (language: string) => void;
};

export const LanguageSelector: React.FC<LanguageSelectorProps> = ({
  currentLanguage,
  onLanguageChange,
}) => {
  const handleLanguageChange = (language: string) => {
    if (language !== currentLanguage) {
      onLanguageChange(language);
    }
  };

  const getLanguageData = (language: string) => {
    if (language === 'lv') {
      return lvData;
    } else if (language === 'en') {
      return enData;
    }
    // Add more language options if needed
    return {};
  };

  const languageData = getLanguageData(currentLanguage);

  return (
    <div className="flex items-center">
      <ul className="lg:flex justify-center items-center gap-x-4">
        <li className="hover:bg-focused rounded-xl px-4 py-2 transition duration-200 font-medium text-base">
          <NavLink title="lol" href="/" />
        </li>
        {/* ...Other menu items */}
      </ul>
      <button
        className="w-10 h-10 mt-1 ml-2"
        onClick={() => handleLanguageChange(currentLanguage === 'lv' ? 'en' : 'lv')}
      >
        <img
          src={currentLanguage === 'lv' ? englishFlagImage : latviaFlagImage}
          alt={currentLanguage === 'lv' ? 'English' : 'Latvian'}
        />
      </button>
    </div>
  );
};

// Navbar.tsx
type NavbarProps = {
  languageData: any; // Update the type of languageData accordingly
};

export const Navbar: React.FC<NavbarProps> = ({ languageData }) => {
  // Use languageData in your Navbar component
  return (
    <nav className="w-full p-4 shadow bg-df">
      <div className="mx-auto flex justify-between items-center">
        <div className="flex items-center">
          <LanguageSelector
            currentLanguage={languageData.language}
            onLanguageChange={language => console.log('Language changed:', language)}
          />
        </div>
      </div>
    </nav>
  );
};
