import { LuStep } from '../components/luStep';
import { Page } from './Page';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import lvData from '../locales/lv.json';
import enData from '../locales/en.json';

export const Home = () => {
  const [currentLanguage, setCurrentLanguage] = useState('lv');
  const languageData: LanguageData =
    currentLanguage === 'lv'
      ? { ...lvData, welcome: 'Laipni lūdzam!' }
      : enData;

  const handleLanguageChange = (language: string) => {
    setCurrentLanguage(language);
  };

  return (
    <Page>
      <div className="container mx-auto px-6 mt-24">
        <div className="py-12">
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-8">
            <div className="sm:col-span-2 flex flex-col justify-center">
              <h1 className="cursor-default text-3xl sm:text-4xl font-bold text-gray-800 leading-tight text-center">
                {languageData && languageData.welcome ? languageData.welcome : ''}
              </h1>
              <hr className="h-1 bg-df from-sky-400 to-sky-600 my-4 rounded" />
              <p className="cursor-default text-gray-600 text-base sm:text-lg font-medium text-center">
                {languageData && languageData.description ? languageData.description : ''}
              </p>
              <div className="flex mt-4 justify-center">
                <Link to="/celsLidzIzvelei">
                  <button className="text-white px-4 py-2 rounded bg-unFocused hover:bg-focused transition font-semibold text-base sm:text-lg">
                    {languageData && languageData.buttonText ? languageData.buttonText : ''}
                  </button>
                </Link>
              </div>
            </div>
            <div className="flex flex-col justify-center">
              <div className="relative">
                {/* start screen icon */}
              </div>
            </div>
          </div>
        </div>
      </div>
      <div>
        <div className="container mx-auto px-6 mt-8">
          <div className="py-12 ">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-8 cursor-default">
              <LuStep
                title={languageData && languageData.step1Title ? languageData.step1Title : ''}
                description={languageData && languageData.step1Description ? languageData.step1Description : ''}
              />
              <LuStep
                title={languageData && languageData.step2Title ? languageData.step2Title : ''}
                description={languageData && languageData.step2Description ? languageData.step2Description : ''}
              />
              <LuStep
                title={languageData && languageData.step3Title ? languageData.step3Title : ''}
                description={languageData && languageData.step3Description ? languageData.step3Description : ''}
              />
              <LuStep
                title={languageData && languageData.step4Title ? languageData.step4Title : ''}
                description={languageData && languageData.step4Description ? languageData.step4Description : ''}
              />
            </div>
          </div>
        </div>
      </div>
    </Page>
  );
};

type LanguageData = {
  greeting: string;
  description: string;
  buttonText: string;
  step1Title: string;
  step1Description: string;
  step2Title: string;
  step2Description: string;
  step3Title: string;
  step3Description: string;
  step4Title: string;
  step4Description: string;
  welcome?: string;
};
