import React from 'react';
import { ReactComponent as Separator } from "../assets/vectors/about-separator.svg";

export const AboutSection: React.FC<AboutSectionProps> = (props) => {
  const columnOrder = props.contentFirst ? "md:order-2 order-1" : "order-1";
  const separatorClass = props.separator ? "pb-4" : "pb-16";

  return (
    <div className="grid md:grid-cols-2 grid-cols-1 gap-4">
      <div className={`flex justify-center items-center px-5 md:mb-10 mb-5 ${columnOrder}`}>
        {props.icon}
      </div>
      <div className={`md:mb-5 mb-2 ${columnOrder} ${separatorClass}`}>
        <div className={`bg-df rounded-3xl pt-5 px-5 md:p-10 text-white ${separatorClass}`}>
          <h2 className="font-bold text-2xl mb-1.5">{props.title}</h2>
          <p>{props.content}</p>
        </div>
      </div>
    </div>
  );
};

interface AboutSectionProps {
  title: string;
  content: string;
  icon: JSX.Element;
  contentFirst?: boolean;
  separator?: boolean;
}
