import React from 'react';
import { ReactComponent as Separator } from "../assets/vectors/about-separator.svg";

export const AboutSection: React.FC<AboutSectionProps> = (props) => {
  const separatorClass = props.separator ? "pb-4" : "pb-16";

  return (
    <div className="grid grid-cols-2 gap-4">
      <div className={`flex flex-col justify-center items-center px-5`}>
        <div className={`bg-df rounded-3xl h-full pt-10 lg:pr-10 px-8 lg:pl-24 text-white ${separatorClass}`}>{props.content}</div>
      </div>
      <div className={`flex flex-col justify-center items-center px-5`}>
        {props.icon}
      </div>
      <div className={`flex flex-col justify-center items-center px-5`}>
        {props.icon}
      </div>
      <div className={`flex flex-col justify-center items-center px-5`}>
        <div className={`bg-df rounded-3xl h-full pt-10 lg:pr-10 px-8 lg:pl-24 text-white ${separatorClass}`}>{props.content}</div>
      </div>
      <div className={`flex flex-col justify-center items-center px-5`}>
        {props.content}
      </div>
      <div className={`flex flex-col justify-center items-center px-5`}>
        {props.icon}
      </div>
    </div>
  );
};

interface AboutSectionProps {
  title: string;
  content: string;
  icon: JSX.Element;
  contentFirst?: boolean;
  separator?: boolean;
}
