import { useState } from 'react';
import latviaFlagImage from "../assets/images/latvijasBiblioteka/latviaFlag.png";
import englishFlagImage from "../assets/images/latvijasBiblioteka/englishFlag.png";
import lvData from "../locales/en.json";
import enData from "../path/to/en.json";

export const LanguageSelector = () => {
  const [currentLanguage, setCurrentLanguage] = useState('lv');
  const languageData = currentLanguage === 'lv' ? lvData : enData;

  const handleLanguageChange = (language) => {
    setCurrentLanguage(language);
  };

  return (
    <div className="flex items-center">
      <button
        className="w-10 h-10 mt-1 mr-2"
        onClick={() => handleLanguageChange('lv')}
      >
        {/* Latvian flag icon */}
        <img src={latviaFlagImage} alt="Latvian" />
      </button>
      <button
        className="w-10 h-10"
        onClick={() => handleLanguageChange('en')}
      >
        {/* English flag icon */}
        <img src={englishFlagImage} alt="English" />
      </button>
      {/* You can use languageData for language-specific content */}
    </div>
  );
};
