import React from 'react';

export const LanguageSelector = ({ currentLanguage, onLanguageChange }) => {
  return (
    <div className="flex items-center">
      <button
        className={`mr-2 ${
          currentLanguage === 'en' ? 'text-focused' : 'text-df-light'
        }`}
        onClick={() => onLanguageChange('en')}
      >
        English
      </button>
      <button
        className={`mr-2 ${
          currentLanguage === 'lv' ? 'text-focused' : 'text-df-light'
        }`}
        onClick={() => onLanguageChange('lv')}
      >
        Latvie≈°u
      </button>
    </div>
  );
};
