import { Page } from './Page';
import thinkingImage from "../assets/images/kursi/thinking.png";

interface BoxProps {
  icon: string;
  description: string;
  buttonTitle: string;
}

function Box({ icon, description, buttonTitle }: BoxProps) {
  return (
    <div className="items-center cursor-default bg-df p-6 rounded-lg shadow-md flex flex-col justify-between">
      <div className="mb-4">
        <img className="w-14 h-14 filter brightness-0 invert" src={icon} alt="Icon" />
      </div>
      <div className="mb-4 text-center">
        <p className="text-white">{description}</p>
      </div>
      <div>
        <button className="px-4 py-2 bg-unFocused hover:bg-focused rounded-xl px-4 py-2 transition duration-200 text-white rounded-lg">{buttonTitle}</button>
      </div>
    </div>
  );
}

export const Kursi = () => {
  return (
    <Page>
      <div className="cursor-default relative flex bg-center justify-center items-center bg-[url('assets/images/celsLidzIzvelei/decision.jpg')] md:h-about h-screen bg-cover bg-no-repeat">
        <div className="absolute top-1/3 left-1/3 transform -translate-x-1/2 -translate-y-1/2">
          <h1 className="text-4xl text-black font-bold text-center font-montserrat">
            Mācību pieredze
          </h1>
        </div>
      </div>
      <div className="cursor-default px-4 md:px-12 lg:px-20">
        <h1 className="text-3xl md:text-4xl mt-12 text-center text-gray-800 font-semibold">
          Pirmsākumi
        </h1>
        <p className="text-base md:text-lg lg:text-xl mt-4 mb-12 text-gray-600">
          What is Lorem Ipsum? Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.
        </p>
      </div>
      <div className="container mx-auto px-6 mb-8 max-w-md">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          <Box
            icon={thinkingImage}
            description="Kursi, kuros radās sarežģītības un vajadzēja ievērojami vairāk pievērsties"
            buttonTitle="Sarežģītākie kursi"
          />
          <Box
            icon={thinkingImage}
            description="Kursi, kuri visvairāk varētu noderēt nākotnē"
            buttonTitle="Noderīgākie kursi"
          />
          <Box
            icon={thinkingImage}
            description="Kursi, kuri radīja interesi un izcēlās citu starpā"
            buttonTitle="Interesantākie kursi"
          />
        </div>
      </div>
    </Page>
  );
};
